    @Test
    public void parseResponse() throws Exception {
        List<Tweet> answers = getAnswer();
        JSONObject jsonData = new JSONObject(data);
        if (jsonData == null) throw new NullPointerException("JSONObjest is null");
        JSONParser parser = new JSONParser();
        List<Tweet> tweets = parser.parse(jsonData);
        Assert.assertTrue(tweets.size() == answers.size());
        for (int i = 0; i < tweets.size(); i++) {
            Tweet tweet = tweets.get(i);
            Tweet answer = answers.get(i);
            if (tweet.date == null) throw new NullPointerException("Date in tweet is null");
            if (tweet.text == null) throw new NullPointerException("Text in tweet is null");
            Assert.assertTrue(tweet.date.compareTo(answer.date) == 0);
            Assert.assertTrue(tweet.text.compareTo(answer.text) == 0);
        }
    }

